mapper.update:
UPDATE PRFBOARD SET 
	TYPE=#{type}, CONTENT=#{content}, RATINGS=#{ratings}, MODIFY_DATE = CURRENT_TIMESTAMP
WHERE bNO = #{bno}

UPDATE PRFBOARD SET 
	READCOUNT = #{readCount}
WHERE bNO = #{bno}

UPDATE PRFBOARD SET 
	REPLYCOUNT = (SELECT COUNT(*) FROM PRFREPLY WHERE BNO = #{bno}) 
WHERE bNO = #{bno};

UPDATE PRFBOARD SET 
	STATUS = 'N' 
WHERE bNO = #{bno}

mapper.select:
SELECT 
	fNO, bNO, ORIGINAL_FILENAME, RENAMED_FILENAME, CREATE_DATE FROM PRFBOARD_ATTACH_FILE
WHERE FNO = #{fno}

SELECT 
	fNO, bNO, ORIGINAL_FILENAME, RENAMED_FILENAME, CREATE_DATE FROM PRFBOARD_ATTACH_FILE
WHERE BNO = #{bno}
